// Pobierz response z poprzedniego kroku
def branchStep = testRunner.testCase.getTestStepByName("Add branch - Request 1")
def response = branchStep.getHttpRequest().getResponse().getContentAsString()

// Parsuj JSON i zapisz ID
def json = new groovy.json.JsonSlurper().parseText(response)
testRunner.testCase.setPropertyValue("branch1_id", json.id.toString())

log.info("Saved branch1_id: " + json.id)

import java.util.UUID

// Generate unique timestamp-based IDs
def timestamp = String.valueOf(System.currentTimeMillis())
def uniqueSuffix = timestamp.substring(timestamp.length()-6) // last 6 digits

// Create unique VIN (17 chars total)
def uniqueVin = "1HGCM82633A" + uniqueSuffix
testRunner.testCase.setPropertyValue("uniqueVin", uniqueVin)

// Create unique registration (8 chars total)  
def uniqueReg = "ABC" + uniqueSuffix.substring(0,5)
testRunner.testCase.setPropertyValue("uniqueReg", uniqueReg)

// Log for debugging
log.info("Generated VIN: " + uniqueVin)
log.info("Generated REG: " + uniqueReg)

import java.util.UUID

// Use UUID for maximum uniqueness
def uuid = UUID.randomUUID().toString().replace('-','')

// Generate unique PESEL - EXACTLY 11 digits
def peselDigits = uuid.substring(0,11).replaceAll("[a-f]", "1") // replace letters with digits
def uniquePesel = peselDigits.substring(0,11) // ensure exactly 11 digits
testRunner.testCase.setPropertyValue("uniquePesel", uniquePesel)

// Generate unique email  
def uniqueEmail = "user" + uuid.substring(0,8) + "@test.com"
testRunner.testCase.setPropertyValue("uniqueEmail", uniqueEmail)

// Generate unique phone (format: +48-XXX-XXX-XXX)
def phoneDigits = uuid.substring(11,20).replaceAll("[a-f]", "2") // only digits
def uniquePhone = "+48-" + phoneDigits.substring(0,3) + "-" + phoneDigits.substring(3,6) + "-" + phoneDigits.substring(6,9)
testRunner.testCase.setPropertyValue("uniquePhone", uniquePhone)

// Generate unique account number (26 digits)
def baseAccount = "98765432109876543210123456"
def accountSuffix = uuid.substring(20,26).replaceAll("[a-f]", "3")
def uniqueAccount = baseAccount.substring(0,20) + accountSuffix
testRunner.testCase.setPropertyValue("uniqueAccount", uniqueAccount)

// Generate unique login
def uniqueLogin = "user" + uuid.substring(0,8)
testRunner.testCase.setPropertyValue("uniqueLogin", uniqueLogin)

// Log for debugging
log.info("Generated PESEL (11 digits): " + uniquePesel + " (length: " + uniquePesel.length() + ")")
log.info("Generated Email: " + uniqueEmail)
log.info("Generated Login: " + uniqueLogin)
log.info("Generated Phone: " + uniquePhone)
log.info("Generated Account (26 digits): " + uniqueAccount + " (length: " + uniqueAccount.length() + ")")

import java.util.UUID
import java.text.SimpleDateFormat
import java.util.Date

// Generate random dates for rental period
def now = new Date()
def startOffset = (int)(Math.random() * 30) // 0-30 days from now
def duration = (int)(Math.random() * 14) + 1 // 1-14 days rental

def startDate = new Date(now.getTime() + (startOffset * 24 * 60 * 60 * 1000L))
def endDate = new Date(startDate.getTime() + (duration * 24 * 60 * 60 * 1000L))

def formatter = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss")
def startDateTime = formatter.format(startDate)
def endDateTime = formatter.format(endDate)

// Generate realistic price (base + random)
def basePrice = 150.0
def randomPrice = basePrice + (Math.random() * 500) // 150-650 range
def finalPrice = Math.round(randomPrice * 100) / 100 // round to 2 decimals

testRunner.testCase.setPropertyValue("orderStartDate", startDateTime)
testRunner.testCase.setPropertyValue("orderEndDate", endDateTime) 
testRunner.testCase.setPropertyValue("orderPrice", String.valueOf(finalPrice))

log.info("Generated Order: " + startDateTime + " to " + endDateTime + ", Price: " + finalPrice)
